def create_2d_grid(input_text):
    side_length = int(np.sqrt(len(input_text)))
    if side_length * side_length < len(input_text):
        side_length += 1
    return np.array(list(input_text)).reshape(side_length, side_length)

def find_xmas_pattern(grid):
    rows,cols = grid.shape
    pattern = ['X','M','A','S']
    found_patterns = []
    directions = [
        (0, 1),   # right
        (1, 0),   # down
        (1, 1),   # diagonal down-right
        (-1, 1),  # diagonal up-right
        (0, -1),  # left
        (-1, 0),  # up
        (1, -1),  # diagonal down-left
        (-1, -1)  # diagonal up-left
    ]
    for start_row in range(rows):
        for start_col in range(cols):
            for dx, dy in directions:
                end_row = start_row + dx * 3
                end_col = start_col + dy * 3

                if (0 <= end_row < rows and 0 <= end_col < cols):
                    current_pattern = []
                    for i in range(4):
                        curr_row = start_row + i * dx
                        curr_col = start_col + i * dy
                        current_pattern.append(grid[curr_row, curr_col])

                    if current_pattern == pattern:
                        found_patterns.append({
                            'start': (start_row, start_col),
                            'direction': (dx, dy),
                            'pattern': ''.join(current_pattern)
                        })

    return found_patterns


